import React from 'react';
import NavBar from './components/NavBar';
import Login from './components/Login'
import Home from './components/Home';
import MovieList from './components/MovieList';
import InMovie from './components/InMovie';
import TVseries from './components/TVseries';
import Cookie, { CookiesProvider, useCookies } from 'react-cookie'
import { BrowserRouter, Route, Switch } from 'react-router-dom';

const App = () => {

    const [cookie] = useCookies()
    return (
        <CookiesProvider>
                    <BrowserRouter>
            {
                !cookie.token ? <Login /> :
                    <Switch>
                        <div className="App">

                            <NavBar />
                            <Route exact path='/' component={Home} />
                            <Route path='/Movielist' component={MovieList} />
                            <Route path='/TVshow' component={MovieList} />
                            <Route path='/movie/:id' component={InMovie} />
                            <Route path='/series/:id' component={TVseries} />

                        </div>
                    </Switch>
            }

        </BrowserRouter>
        </CookiesProvider>

    );
}

export default App;
