const initialState = {
    movies: 'bahadır',
    movieError: ''
}

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'GET_USER_LIST':
            return {
                ...state,
                movies: action.payload
            }
        case 'GET_USER_LIST_ERROR':
            return {
                ...state,
                movieError: action.payload
            }
        default:
            return state
    }
}

export default rootReducer;
