import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import { Link } from 'react-router-dom';

const useStyles = makeStyles({
    root: {
        width: "100%",
        position: 'fixed',
        bottom: 0,
    },
});

const BottomNav = () => {

    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    return (
        <BottomNavigation
            value={value}
            onChange={(event, newValue) => {
                setValue(newValue);
            }}
            showLabels
            className={classes.root}
        >
            <Link to="/"><BottomNavigationAction label="Home" icon={<RestoreIcon />} /></Link>
            <Link to="/Movielist"><BottomNavigationAction label="Movie" icon={<FavoriteIcon />} /></Link>
            <Link to="/TVshow"><BottomNavigationAction label="Series" icon={<LocationOnIcon />} /></Link>
            <Link to="/people"><BottomNavigationAction label="People" icon={<LocationOnIcon />} /></Link>
        </BottomNavigation>
    );
}

export default BottomNav;
