import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { singleMovie, leadActor } from '../actions';
import '../css/NavBar.css'
import LeadActor from './LeadActor';

const InMovie = (props) => {

    const movieID = props.match.params.id

    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(singleMovie(movieID))
        dispatch(leadActor(movieID))
    },[])

    const getSingle = useSelector((state) => state.singleMovie)
    const getLeadActor = useSelector((state) => state.leadActor)
    console.log(getLeadActor)
    return (
        <div>
            <div>
            </div>          
            <div className="mainInMovieBody" style={{ backgroundImage: `url(https://www.themoviedb.org/t/p/w1280${getSingle?.backdrop_path})`}}>            
                <div className="inMovieBody">
                    <div className="posterPath"><img src={`https://www.themoviedb.org/t/p/w1280/${getSingle?.poster_path}`} height="100%" width="100%"/></div>
                    <div className="movieDescription">
                        <div className="movieTitle"><h2>{getSingle?.title}</h2>
                        <div className="dateAndGenres">
                            <p>{getSingle?.release_date}</p> {getSingle?.genres.map((gen) => {
                            return(
                                <span style={{marginRight: "7px"}} key={gen.id}>{gen.name},</span>
                            )
                        })}


                        </div>
                        </div>
                        <div className="movieScore">
                            <div className="moviePoint">{getSingle?.vote_average}</div>
                            Üye <br/> Puanları

                            </div>
                        <div className="movieSummary">
                            <h3>Özet</h3>
                            <p>{getSingle?.overview}</p>
                        </div>
                        <div className="movieAuthors">
                            <div className="author">
                                <p>Chris Columbus</p>
                                <p>Director</p>
                            </div>
                            <div className="author">
                                <p>Chris Columbus</p>
                                <p>Director</p>
                            </div>
                            <div className="author">
                                <p>Chris Columbus</p>
                                <p>Director</p>
                            </div>
                        </div>
                    </div>
                </div>

            </div>
            {
                getLeadActor?.map((actor) => {
                    return(
                        <div>
                        <LeadActor name={actor.name} photo={actor.profile_path} role={actor.character}/>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default InMovie;
