import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom'
import '../css/NavBar.css'
import { getAPI, getTV } from '../actions';

const MovieList = (props) => {
    const dispatch = useDispatch()

    const listParams = props.match.path;
    console.log(props)

    useEffect(() => {
        if (listParams === '/Movielist') {
            dispatch(getAPI())
        }
        if (listParams === '/TVshow') {
            dispatch(getTV())
        }


    }, [listParams])


    const movies = useSelector((state) => state.movies)
    console.log(movies)
    return (
        <div className="movieBody">             
            {
                movies.map((movie) => {
                    return (

                        <div className="movieCard" key={movie?.id}>
                            <div className="cardPic"><img src={`https://www.themoviedb.org/t/p/w1280/${movie?.poster_path}`} height="100%" width="100%"/></div>
                            <Link to={`/card/${movie?.id}`} style={{textDecoration: 'none', color: 'black'}}>
                                <div className="cardInText">
                                    <h2>{
                                    movie?.original_title ?
                                    movie?.original_title?.substr(0, 28)
                                    :
                                    movie?.original_name?.substr(0, 28)
                                    }
                                    {
                                    (movie?.original_title?.length > 27 || movie?.original_name?.length > 27) ? '...' : ''
                                    }
                                    </h2>
                                    <p>{movie?.overview?.substr(0, 60)}...</p>
                                    <span>{movie?.vote_average} View</span>
                                </div>
                            </Link>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default MovieList;
