import React, { useState } from 'react';
import { Button, Input, TextField } from '@material-ui/core';

const SearchInput = ({subProps}) => {

    const [searchValue, setsearchValue ] = useState("")


    const handleChange = (e) => {
        setsearchValue(e.target.value)
        subProps.history.push("/search")
    }

    return(
        <div>
            <div className="searchRowBody">
                <form
                onSubmit={handleChange}
                >
            <Input id="standard-search" 
            placeholder="Film, dizi, kişi ara..." 
            type="search" 
            fullWidth="true"
            disableUnderline="true"
            />
            <Button onChange={handleChange} variant="contained" color="primary" style={{ borderRadius: "20px"}}>Search</Button>
            </form>
            </div>
        </div>
    )
}

export default SearchInput;
