const initialState = {
    movies: [],
    singleMovie: null
}

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'GET_MOVIE_LIST':
            return {
                ...state,
                movies: action.payload
            }

        case 'GET_TV_LIST':
            return {
                ...state,
                movies: action.payload
            }

        case 'GET_SINGLE_MOVIE' :
            return {
                ...state,
                singleMovie: action.payload
            }

        case 'GET_SINGLE_TV' :
            return {
                ...state,
                singleMovie: action.payload
            }    

        default:
            return state
    }
}

export default rootReducer;
