import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { singleMovie } from '../actions'
import '../css/NavBar.css'

const InMovie = (props) => {

    const movieID = props.match.params.id

    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(singleMovie(movieID))
    },[])

    const getSingle = useSelector((state) => state.singleMovie)

    return (
        <div className>
            <div className="mainInMovieBody">
                <div className="inMovieBody">
                    <div className="posterPath"><img src="#"/></div>
                    <div className="movieDescription">
                        <h2>{getSingle?.original_title}</h2>
                        <p> Movie Description</p>
                    </div>
                </div>

            </div>
        </div>
    )
}

export default InMovie;
