import React, { useState } from 'react';
import { useCookies } from 'react-cookie'
import Logo from '../img/logo.png'
import Data from '../user.json'
import { Button, Grid, InputAdornment, TextField } from '@material-ui/core'
import '../css/NavBar.css'
import { AccountCircle } from '@material-ui/icons';


const Login = () => {

    const [cookie, setCookie, removeCookie] = useCookies()

    const [loginForm, setloginForm] = useState({
        email: "",
        password: ""
    })

    const handleSubmit = (event) => {
        event.preventDefault()
        if(loginForm.email === Data.email && loginForm.password === Data.password) {
           setCookie('token', 'ahadir')
           setloginForm({
               email: "",
               password: ""
           })
        }else {
            setloginForm({
                email: "",
                password: ""
            })
        }
    }

    return(
        <Grid container style={{ minHeight: "100vh"}}>
            <Grid item xs={12} sm={6}>
                <img src="https://cdn.pixabay.com/photo/2018/07/06/19/48/charles-chaplin-3521070_960_720.jpg" style={{ width:"100%", height:"100%", objectFit:"cover"}} alt="brand"/>
            </Grid>
            <Grid container xs={12}
             sm={6} 
             alignItems="center"
              direction="column"
              justify="space-between"
              style={{padding: 10}}> 
                <div/>
                <div style={{display:"flex", flexDirection:"column", maxWidth:400, minWidth:300}}>
                    <Grid container justify="center">
                    <img src={Logo} alt="logo" width={150}/>
                    </Grid>
                    <TextField 
                    label="Kullanıcı adı" 
                    margin="normal"
                    InputProps={{ startAdornment: <InputAdornment><AccountCircle/></InputAdornment>}}
                    />
                    <TextField 
                    label="Şifre"
                    margin="normal"
                    InputProps={{ }}
                     />
                    <div style={{ height:20 }}/>
                    <Button color="primary" variant="contained">
                        Giriş yap
                    </Button>
                    <div style={{ height:20 }}/>
                    <Button>
                       Kayıt ol
                    </Button>
                </div>
                <div/>
            </Grid>
        </Grid>
    )
}

export default Login;
