import React, { Fragment, useEffect } from 'react';
import NavBar from './components/NavBar';
import Login from './components/Login'
import Home from './components/Home';
import MovieList from './components/MovieList';
import InMovie from './components/InMovie';
import TVseries from './components/TVseries';
import Cookie, { CookiesProvider, useCookies } from 'react-cookie'
import { BrowserRouter, Redirect, Route, Switch } from 'react-router-dom';

const App = (props) => {

    const [cookies] = useCookies(['token'])
    useEffect(()=>{

    },[props?.match?.url])
    return (
        <CookiesProvider>
                    <BrowserRouter>
                    <Switch>
                        <div className="App">
                            {
                                !cookies.token ?
                                <Route exact path='/login' component={Login} />
                                :
                                <Redirect to='/'/>
                            }
                            {
                                cookies.token &&
                                <Fragment>
                                    <NavBar />
                            <Route exact path='/' component={Home} />
                            <Route path='/Movielist' component={MovieList} />
                            <Route path='/TVshow' component={MovieList} />
                            <Route path='/movie/:id' component={InMovie} />
                            <Route path='/series/:id' component={TVseries} />
                                </Fragment>
                            }




                        </div>
                    </Switch>

        </BrowserRouter>
        </CookiesProvider>

    );
}

export default App;
