import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { CircularProgressbar, buildStyles, } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import { Link } from 'react-router-dom'
import '../css/NavBar.css'
import { getAPI, getTV } from '../actions';
import Pagenation from './Pagenation';
import { Grid } from '@material-ui/core';

const Series = (props) => {
    const dispatch = useDispatch()
    const passedSeriesUrl = props.match.url;

    useEffect(() => {
            dispatch(getTV())
    }, [])


    const series = useSelector((state) => state.series)
    return (
        <div>
                   <div className="movieBody">             
            {
                series?.results?.map((seri) => {
                    return (

                        <div className="movieCard">
                        <Link to={`/series/${seri?.id}`}>
                        <div className="picAndProgres">
                        <div className="cardPic"><img src={`https://www.themoviedb.org/t/p/w1280/${movie?.poster_path}`} height="100%" width="100%"/></div>
                        <div className="cardProgress">
                        <CircularProgressbar
                            value={seri?.vote_average}
                            maxValue="10"
                            text={`${seri?.vote_average}%`}
                            background
                            backgroundPadding={6}
                            styles={buildStyles({
                                textSize:"25",
                                backgroundColor: "#081c22",
                                textColor: "#fff",
                                pathColor: "#21d07a",
                                trailColor: "transparent"
                            })}
                        />
                        </div>
                        </div>
                        <div className="cardInText">
                            <h3>{movie?.original_title}</h3>
                            <p>{movie?.release_date}</p>
                        </div>
                        </Link>
                    </div>
                    )
                })
            }
        </div>
        <Grid
            container
            justify="center"
            alignItems="center"
            >
               <Pagenation passedUrl={passedSeriesUrl}/> 
            </Grid>


        </div>

    )
}

export default Series;
