import React, { useEffect } from 'react';
import { isMobile } from 'react-device-detect';
import NavBar from './NavBar';
import BottomNav from './BottomNav';
import { Grid, Paper } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import '../css/NavBar.css';


const MyFavorites = () => {

    const addToFav = useSelector((state) => state.likedMovie);

    console.log(addToFav)

    return (
        <div>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <NavBar />
                </Grid>
                <Grid item xs={2}></Grid>
                <Grid container spacing={3} xs={8}>
                    {
                        addToFav.map((res) => {
                            return (
                                <Grid item xs={3}>
                                    <div className="favoriteCard">
                                        <div stlye={{position:"static"}}>
                                        <div className="favoritePic"><img src={`https://www.themoviedb.org/t/p/w1280/${res?.poster_path}`} height="100%" width="100%"/></div>
                                        </div>
                                        <div className="favoriteText">
                                            <h3>{res?.original_title.substr(0,50)}</h3>
                                            <p>{res?.release_date}</p>
                                        </div>
                                    </div>
                                </Grid>
                            )
                        })
                    }
                </Grid>
                <Grid item xs={2}></Grid>
            </Grid>
        </div>
    )
}

export default MyFavorites;
