import { Link } from 'react-router-dom'
import '../css/NavBar.css'
import Pagenation from './Pagenation';
import { Grid } from '@material-ui/core';
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPeople } from '../actions';

const PeopleList = (props) => {
    const dispatch = useDispatch();
    const passedMovieUrl = props.match.url;


    useEffect(() => {
        dispatch(getPeople())
    }, [])

    const people = useSelector((state) => state.people);

    console.log(people)

    return (
        <div>
            <div className="movieBody">
                {
                    people?.results?.map((person) => {
                        return (
                            <div className="movieCard">
                                <Link to={`/people/${person?.id}`}>
                                    <div className="picAndProgres">
                                        <div className="cardPic"><img src={`https://www.themoviedb.org/t/p/w1280/${person?.poster_path}`} height="100%" width="100%" /></div>
                                    </div>
                                    <div className="cardInText">
                                        <h3>{person?.original_title}</h3>
                                        <p>{person?.release_date}</p>
                                    </div>
                                </Link>
                            </div>
                        )
                    })
                }
            </div>
            <Grid
                container
                justify="center"
                alignItems="center"
            >
                <Pagenation passedUrl={passedMovieUrl} />
            </Grid>


        </div>
    )
}

export default PeopleList;
