import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import HomeIcon from '@material-ui/icons/Home';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import { Link } from 'react-router-dom';

const useStyles = makeStyles({
  root: {
    width: "100%",
    position: 'fixed',
    bottom: 0,
  },
});

const BottomNav = () => {

    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    return (
        <BottomNavigation
          value={value}
          onChange={(event, newValue) => {
            setValue(newValue);
          }}
          showLabels
          className={classes.root}
        >
          <BottomNavigationAction component={Link} to="/" label="Home" icon={<HomeIcon />} />
          <BottomNavigationAction component={Link} to="/Movielist" label="Movie" icon={<FavoriteIcon />} />
          <BottomNavigationAction component={Link} to="/TVshow" label="Series" icon={<LocationOnIcon />} />
          <BottomNavigationAction component={Link} to="/people" label="People" icon={<LocationOnIcon />} />
        </BottomNavigation>
      );
}

export default BottomNav;
