import React, { useState } from 'react';
import { Button, Input, TextField } from '@material-ui/core';

const SearchInput = ({ subProps }) => {

    const [searchValue, setsearchValue] = useState("")


    const handleChange = (e) => {
        e.preventDefault()
        console.log(subProps)
        if(subProps?.location?.pathname === "/"){
            subProps?.history?.push({
                pathname: '/search',
                search: searchValue,
              })
        }
        if(subProps?.location?.pathname === "/search"){
            subProps?.history?.push({
                search: searchValue,
              })
        }
    }


    return (
        <div>
            <form onSubmit={handleChange}>
                <div className="searchRowBody">
                    <Input id="standard-search"
                        placeholder="Film, dizi, kişi ara..."
                        type="search"
                        fullWidth="true"
                        disableUnderline="true"
                        onChange={(e) => setsearchValue(e.target.value)}
                        value={searchValue}
                        />
                    <Button onClick={handleChange} variant="contained" color="primary" style={{ borderRadius: "20px" }}>Search</Button>
                </div>
            </form>
        </div>
    )
}

export default SearchInput;
